INSERT INTO `typemanagment` (`id`, `name`, `descr`, `type`) VALUES
(1, 'Управляющая компания', '<br>&emsp;<b>Управляющая компания</b> — коммерческая организация, юридическое лицо, осуществляющее доверительное управление имуществом других физических и юридических лиц, переданным в соответствии с договором доверительного управления.<br>&emsp;По договору доверительного управления имуществом одна сторона (учредитель управления) передает другой стороне (доверительному управляющему) на определенный срок имущество в доверительное управление, а другая сторона обязуется осуществлять управление этим имуществом в интересах учредителя управления или указанного им лица (выгодоприобретателя).<br>&emsp;Как правило, для договора доверительного управления имуществом необходима простая письменная форма, за исключением случая передачи в доверительное управление недвижимого имущества. Несоблюдение формы договора доверительного управления имуществом влечет его ничтожность (ст. 1017 ГК РФ).', 'residential'),
(2, 'Товарищество собственников жилья', '<br>&emsp;<b>Товарищество собственников жилья</b> (ТСЖ) — юридическое лицо, некоммерческая организация, созданная на основе объединения собственников помещений многоквартирного дома или собственников соседних участков с жилыми строениями (домами) для совместного управления теми помещениями этого дома и землями, которые находятся в совместном владении и пользовании, а также для ведения хозяйственной деятельности в таком доме и на земле, находящейся в совместном владении, в форме эксплуатации общего имущества, строительства дополнительных помещений и объектов общего имущества, а также сдачи в аренду, внаём части общего имущества в многоквартирном доме или общего земельного участка.<br>&emsp;ТСЖ одна из форм местного самоуправления, позволяет инициативной группе решать проблемы, позволяет широкому кругу заинтересованных лиц принимать участие в решении вопросов функционирования дома.<br>&emsp;Но на практике часто никто не хочет заниматься хозяйственными нуждами всего дома, люди чувствуют себя собственниками только своей квартиры. Среди собственников встречаются не желающие платить членские взносы на содержание дома, при этом существует мало способов воздействия на неплательщиков. ', 'residential'),
(3, 'Жилищно-строительный кооператив', '<br>&emsp;<b>Жилищно-строительный кооператив</b> (жилищный кооператив, ЖСК) — объединение людей или организаций, с целью строительства жилья, а также для управления жильем. <br>&emsp;В России сохранилось множество ЖСК со времён СССР, и, хотя при принятии нового Жилищного кодекса Российской Федерации была сделана попытка обязать их всех осуществить преобразование в Товарищество собственников жилья, соответствующая поправка была отменена. В большинстве случаев в современной России ЖСК — это эксплуатирующая организация в отношении жилищного фонда. Создание ЖСК для целей строительства было связано с многочисленными аферами на рынке недвижимости. ЖСК как организационно-правовую форму объединения граждан для строительства используют обманутые дольщики, желающие достроить своё жильё после того, как строительные компании, привлекавшие их средства, прекращают строительство.', 'residential'),
(4, 'Управление собственником недвижимости', '<br>&emsp;<b>Управление недвижимостью собственником</b> предполагает конкретные действия владельца в той или иной ситуации. <br>&emsp;Основной задачей является управление недвижимостью с целью получения максимальной прибыли от эксплуатации зданий и повышение рыночной стоимости объекта, наряду со всесторонним соблюдением интересов как владельцев, так и арендаторов.', 'commercial'),
(5, 'Доверительное управление', '<br>&emsp;<b>Доверительное управление</b> (или траст — от англ. trust) — это система отношений, при которой имущество, первоначально принадлежащее учредителю, передается в распоряжение доверительного собственника (управляющего или попечителя), но доход с него получают выгодоприобретатели (бенефициары). Учредитель (который может одновременно быть и выгодоприобретателем и/или управляющим) в рамках специального соглашения передает принадлежащие ему ценности под контроль попечителя, который обязан совершать с ними операции, приносящие выгодоприобретателям максимальную прибыль или соответствующие другим инструкциям учредителя.<br>&emsp;В договоре доверительного управления имуществом должны быть указаны:<br>&emsp; - состав имущества, передаваемого в доверительное управление;<br>&emsp; - наименование юридического лица или имя гражданина, в интересах которых осуществляется управление имуществом (учредителя управления или выгодоприобретателя);<br>&emsp; - размер и форма вознаграждения управляющему, если выплата вознаграждения предусмотрена договором;<br>&emsp; - срок действия договора.', 'commercial');

